.segment "CODE"

.proc screenFactory

    LDA scrollPosition
    CMP #$FD
    BEQ incrementScreenCount
    BNE incrementScreenDone

  incrementScreenCount:
    INC screenCount
    JMP incrementScreenDone

  incrementScreenDone:

  resetScreens:

  loadScreens:
    LDA screenCount
    CMP #00
    BEQ loadScreen1
    CMP #01
    BEQ loadScreen2
    CMP #02
    BEQ loadScreen3
    CMP #03
    BEQ loadScreen4
    CMP #04
    BEQ resetScreen

  loadScreen1:
    LDX #<map_level_1_2
    STX lowMapByte
    LDX #>map_level_1_2
    STX highMapByte

    LDX #<attributeTableLevel1Page2
    STX lAB
    LDX #>attributeTableLevel1Page2
    STX hAB

    JMP loadScreenDone

  loadScreen2:
    LDX #<map_level_1_page_1
    STX lowMapByte
    LDX #>map_level_1_page_1
    STX highMapByte

    LDX #<attributeTableLevel1Page3
    STX lAB
    LDX #>attributeTableLevel1Page3
    STX hAB

    JMP loadScreenDone

  loadScreen3:
    LDX #<map_level_1_page_2
    STX lowMapByte
    LDX #>map_level_1_page_2
    STX highMapByte
    JMP loadScreenDone

  loadScreen4:
    LDX #<map_level_1_page_3
    STX lowMapByte
    LDX #>map_level_1_page_3
    STX highMapByte
    JMP loadScreenDone

  resetScreen: 
    LDX #00
    STX screenCount
    JMP resetScreens

  loadScreenDone:
    RTS
.endproc

.proc loadStageOneBackground
    LDA #<map_level_1
    STA mapLoByte       ; put the low byte of the address of background into pointer
    LDA #>map_level_1
    STA mapHiByte       ; put the high byte of the address into pointer
.endproc